<?php
 class Walker_CategoryDropdown extends Walker { public $tree_type = 'category'; public $db_fields = array( 'parent' => 'parent', 'id' => 'term_id', ); public function start_el( &$output, $data_object, $depth = 0, $args = array(), $current_object_id = 0 ) { $category = $data_object; $pad = str_repeat( '&nbsp;', $depth * 3 ); $cat_name = apply_filters( 'list_cats', $category->name, $category ); if ( isset( $args['value_field'] ) && isset( $category->{$args['value_field']} ) ) { $value_field = $args['value_field']; } else { $value_field = 'term_id'; } $output .= "\t<option class=\"level-$depth\" value=\"" . esc_attr( $category->{$value_field} ) . '"'; if ( (string) $category->{$value_field} === (string) $args['selected'] ) { $output .= ' selected="selected"'; } $output .= '>'; $output .= $pad . $cat_name; if ( $args['show_count'] ) { $output .= '&nbsp;&nbsp;(' . number_format_i18n( $category->count ) . ')'; } $output .= "</option>\n"; } } 